package aula2;

public class Exemplo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int valor1 = 50;
		int valor2 = 2;
		
		System.out.println(valor1 + " * " + valor2 + " = " + (valor1 * valor2));
		System.out.println(valor1 + " + " + valor2 + " = " + (valor1 + valor2));
		System.out.println(valor1 + " - " + valor2 + " = " + (valor1 - valor2));
		System.out.println(valor1 + " resto da divisão " + valor2 + " = " + (valor1 % valor2));
		System.out.println(valor1 + " / " + valor2 + " = " + (valor1 / valor2));
		
	}

}
